xquery version "1.0-ml";

declare namespace xhtml = "http://www.w3.org/1999/xhtml";

declare function local:pageContent($date as xs:date, $remainingURI as xs:string) as node()? {
  fn:doc(concat("/nsmissal/", $date, $remainingURI, ".xhtml"))//xhtml:body/xhtml:div[@id eq "contenuto"]/xhtml:div[@class eq "inner"]
};

declare function local:dayCheck($date as xs:date, $breviaryToc as node()?) as xs:unsignedLong {
  xdmp:hash64(xdmp:quote(
    <links>{(
      if (exists($breviaryToc)) then $breviaryToc/xhtml:a 
      else local:pageContent($date, "/breviary/toc.xhtml")/xhtml:a,
      local:pageContent($date, "/readings/toc.xhtml")/xhtml:a
    )}</links>))
};

let $basisCheck := local:dayCheck(xs:date("2015-01-01"), ())
(: 
let $date := xs:date("2016-05-29") (: This one has lots of other stuff beyond citations :)
:) 
let $date := xs:date("2016-12-25")
let $btoc := local:pageContent($date, "/breviary/toc")
let $dayCheck := local:dayCheck($date, $btoc)
let $btocDate := $btoc/xhtml:p[1]/text()
let $dateInBtocFormat := fn:format-date($date, "[FNn], [D] [MNn] [Y0001]")

let $_ := 
  if ($dayCheck ne $basisCheck) then  
    fn:error(xs:QName("ERROR"), concat("toc checksum mismatch basis *", $basisCheck, "* today *", $dayCheck, "*"))
  else if ($btocDate ne $dateInBtocFormat) then 
    fn:error(xs:QName("ERROR"), concat("date mismatch btoc *", $btocDate, "* today *", $dateInBtocFormat, "*"))   
  else ()

let $openingAntiphon := local:pageContent($date, "/readings/opening_antiphon_collect")//xhtml:span[@class eq "rubrica"]/text()
let $communionAntiphon := local:pageContent($date, "/readings/communion_antiphon_prayer")//xhtml:span[@class eq "rubrica"]/text()
let $readings := local:pageContent($date, "/readings/readings")//xhtml:span[@class eq "titolo" or @class eq "citazione"]/text()

return ( 
  "opening",
  $openingAntiphon,
  "communion",
  $communionAntiphon,
  "readings",
  $readings
)
